import React from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Tooltip, Legend } from "chart.js";
ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

export default function AnalyticsPanel({stats, complaints}) {
  const categories = {};
  complaints.forEach(c => { categories[c.category] = (categories[c.category] || 0) + 1; });

  const data = {
    labels: Object.keys(categories),
    datasets: [{ label: 'Complaints by category', data: Object.values(categories) }]
  };

  return (
    <div className="analytics">
      <h3>Analytics</h3>
      <div className="kpi-cards">
        <div>Total: {stats.total ?? 0}</div>
        <div>Pending: {stats.pending ?? 0}</div>
      </div>
      <div style={{height:200}}>
        <Bar data={data}/>
      </div>
    </div>
  );
}
