import React, { useState } from "react";
import { GoogleMap, Marker, InfoWindow, useLoadScript } from "@react-google-maps/api";

const containerStyle = {
  width: "100%",
  height: "100%",
};

const defaultCenter = { lat: 28.6, lng: 77.2 };

export default function MapView({ complaints }) {
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY", // ðŸ”‘ Replace with your API key
  });

  const [selected, setSelected] = useState(null);

  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <GoogleMap mapContainerStyle={containerStyle} center={defaultCenter} zoom={12}>
      {complaints.map((c) => (
        <Marker
          key={c.id}
          position={{ lat: c.lat, lng: c.lng }}
          onClick={() => setSelected(c)}
        />
      ))}

      {selected && (
        <InfoWindow
          position={{ lat: selected.lat, lng: selected.lng }}
          onCloseClick={() => setSelected(null)}
        >
          <div>
            <strong>{selected.title}</strong>
            <p>Category: {selected.category}</p>
            <p>Status: {selected.status}</p>
            {selected.image && (
              <img
                src={`http://localhost:8000/uploads/${selected.image}`}
                alt="Complaint"
                style={{ width: "200px", borderRadius: "6px", marginTop: "6px" }}
              />
            )}
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}
