import React, { useState, useRef } from "react";

export default function VideoComplaintForm({ refreshComplaints }) {
  const [recording, setRecording] = useState(false);
  const [videoBlob, setVideoBlob] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [status, setStatus] = useState("");

  const mediaRecorderRef = useRef(null);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const chunks = useRef([]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      videoRef.current.srcObject = stream;
      streamRef.current = stream;

      const mediaRecorder = new MediaRecorder(stream, { mimeType: "video/webm" });
      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) chunks.current.push(e.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks.current, { type: "video/webm" });
        setVideoBlob(blob);
        setPreviewUrl(URL.createObjectURL(blob));
        chunks.current = [];
      };

      mediaRecorder.start();
      setRecording(true);
      setStatus("Recording...");
    } catch (err) {
      console.error(err);
      setStatus("Camera access denied.");
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && streamRef.current) {
      mediaRecorderRef.current.stop();
      streamRef.current.getTracks().forEach(track => track.stop());
      setRecording(false);
      setStatus("Recording stopped.");
    }
  };

  const captureLocation = () => {
    if (!navigator.geolocation) return setStatus("Geolocation not supported.");
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude });
        setStatus("Location captured.");
      },
      (err) => setStatus("Failed to fetch location.")
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!videoBlob) return setStatus("Please record a video.");
    if (!location.lat) return setStatus("Please capture location.");

    const formData = new FormData();
    formData.append("video", videoBlob, "complaint.webm");
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await fetch("http://localhost:8000/complaints", { method: "POST", body: formData });
      if (!res.ok) throw new Error(`Server error: ${res.statusText}`);
      const data = await res.json();

      if (data.status === "success") {
        setStatus("‚úÖ Complaint submitted!");
        // Add new complaint to parent state immediately
        refreshComplaints(data.complaint);
        setVideoBlob(null);
        setPreviewUrl(null);
        setLocation({ lat: null, lng: null });
      } else {
        setStatus(`‚ùå Failed: ${data.detail || "Unknown error"}`);
      }
    } catch (err) {
      console.error(err);
      setStatus(`‚ö†Ô∏è Error submitting complaint: ${err.message}`);
    }
  };

  return (
    <form className="video-complaint-form" onSubmit={handleSubmit}>
      <h3>Submit Video Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Infrastructure">Infrastructure</option>
          <option value="Pothole">Pothole</option>
          <option value="Garbage">Garbage</option>
        </select>
      </label>

      <div>
        <video ref={videoRef} autoPlay muted style={{ width: "100%", borderRadius: "8px" }}></video>
        {previewUrl && (
          <video src={previewUrl} controls style={{ width: "100%", marginTop: "8px", borderRadius: "8px" }}></video>
        )}
      </div>

      <div>
        {!recording ? (
          <button type="button" onClick={startRecording}>üé• Start Recording</button>
        ) : (
          <button type="button" onClick={stopRecording}>‚èπ Stop Recording</button>
        )}
      </div>

      <button type="button" onClick={captureLocation}>üìç Capture Location</button>

      {location.lat && location.lng && (
        <p>Location: {location.lat.toFixed(4)}, {location.lng.toFixed(4)}</p>
      )}

      <button type="submit">Submit Complaint</button>
      <p>{status}</p>
    </form>
  );
}
