import React, { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function ComplaintForm({ refreshComplaints }) {
  const [location, setLocation] = useState(null);
  const [category, setCategory] = useState("Sanitation");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => {
        alert("Location access denied.");
        console.error(err);
      }
    );
  }, []);

  const handleSubmit = async () => {
    if (!location) {
      alert("Location not available.");
      return;
    }

    const formData = new FormData();
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await axios.post(`${API}/complaints`, formData);
      alert("Complaint submitted!");
      refreshComplaints(res.data.complaint);
    } catch (err) {
      console.error("Submission error:", err);
      alert("Failed to submit complaint.");
    }
  };

  return (
    <div className="video-complaint-form">
      <h3>Submit Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Water">Water</option>
          <option value="Roads">Roads</option>
        </select>
      </label>

      <button onClick={handleSubmit}>üìç Submit Complaint</button>
    </div>
  );
}
