import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
    const [complaints, setComplaints] = useState([]);
    const [analytics, setAnalytics] = useState({
        total: 0,
        pending: 0,
        resolved: 0
    });

    // Fetch complaints and analytics data from FastAPI
    useEffect(() => {
        async function fetchData() {
            try {
                // Fetch complaints from the backend
                const complaintsResponse = await axios.get('http://localhost:8000/complaints');
                const analyticsResponse = await axios.get('http://localhost:8000/analytics');

                // Set the data to state
                setComplaints(complaintsResponse.data);
                setAnalytics(analyticsResponse.data);
            } catch (error) {
                console.error("Error fetching data from API", error);
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            <h1>Municipal Dashboard</h1>
            <div>
                <h2>Analytics</h2>
                <p>Total: {analytics.total}</p>
                <p>Pending: {analytics.pending}</p>
                <p>Resolved: {analytics.resolved}</p>
            </div>
            <div>
                <h2>Complaints</h2>
                <ul>
                    {complaints.map(complaint => (
                        <li key={complaint.id}>
                            <p>Title: {complaint.title}</p>
                            <p>Category: {complaint.category}</p>
                            <p>Status: {complaint.status}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
