import React, { useState } from "react";

export default function VideoComplaintForm() {
  const [video, setVideo] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [status, setStatus] = useState("");

  // Get location from browser
  const captureLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          });
        },
        (err) => {
          console.error("Location error:", err);
          setStatus("Failed to fetch location.");
        }
      );
    } else {
      setStatus("Geolocation not supported.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!video || !location.lat || !location.lng) {
      setStatus("Please upload video and allow location access.");
      return;
    }

    const formData = new FormData();
    formData.append("video", video);
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await fetch("http://localhost:8000/complaints", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (data.status === "success") {
        setStatus("Video complaint submitted!");
      } else {
        setStatus("Failed to submit.");
      }
    } catch (err) {
      console.error(err);
      setStatus("Error submitting complaint.");
    }
  };

  return (
    <form className="video-complaint-form" onSubmit={handleSubmit}>
      <h3>Submit Video Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Infrastructure">Infrastructure</option>
          <option value="Pothole">Pothole</option>
          <option value="Garbage">Garbage</option>
        </select>
      </label>

      <label>
        Upload Video:
        <input
          type="file"
          accept="video/*"
          onChange={(e) => setVideo(e.target.files[0])}
        />
      </label>

      <button type="button" onClick={captureLocation}>
        Capture Location
      </button>

      {location.lat && location.lng && (
        <p>
          üìç Location Captured: {location.lat.toFixed(4)},{" "}
          {location.lng.toFixed(4)}
        </p>
      )}

      <button type="submit">Submit Complaint</button>
      <p>{status}</p>
    </form>
  );
}
