import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function ComplaintForm({ refreshComplaints }) {
  const [location, setLocation] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  /* ---------------- CAMERA STATES ---------------- */
  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const [recording, setRecording] = useState(false);
  const [stream, setStream] = useState(null);

  /* ---------------- GET LIVE LOCATION ---------------- */
  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation not supported");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => {
        alert("Location access denied.");
        console.error(err);
      }
    );
  }, []);

  /* ---------------- START CAMERA ---------------- */
  useEffect(() => {
    async function startCamera() {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: true,
        });
        setStream(mediaStream);
        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;
        }
      } catch (err) {
        console.error("Camera error:", err);
      }
    }

    startCamera();

    return () => {
      stream?.getTracks().forEach((t) => t.stop());
    };
  }, []);

  /* ---------------- RECORD VIDEO ---------------- */
  const startRecording = () => {
    chunksRef.current = [];
    const recorder = new MediaRecorder(stream);

    recorder.ondataavailable = (e) => chunksRef.current.push(e.data);

    recorder.onstop = () => {
      const blob = new Blob(chunksRef.current, { type: "video/webm" });
      const file = new File([blob], "complaint-video.webm", {
        type: "video/webm",
      });
      setVideo(file);
    };

    recorder.start();
    mediaRecorderRef.current = recorder;
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  /* ---------------- SUBMIT HANDLER ---------------- */
  const handleSubmit = async () => {
    if (!location) {
      alert("Location not available.");
      return;
    }

    if (!description.trim()) {
      alert("Please enter a description.");
      return;
    }

    const formData = new FormData();
    formData.append("category", category);
    formData.append("description", description);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);
    if (image) formData.append("image", image);
    if (video) formData.append("video", video);

    try {
      setLoading(true);

      const res = await axios.post(`${API}/complaints`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ Complaint submitted successfully!");

      if (refreshComplaints && res.data?.complaint) {
        refreshComplaints(res.data.complaint);
      }

      // Reset
      setDescription("");
      setImage(null);
      setVideo(null);
    } catch (err) {
      console.error("Submission error:", err);
      alert("‚ùå Failed to submit complaint.");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="complaint-layout">
      {/* FORM */}
      <div className="video-complaint-form">
        <h3>Submit Complaint</h3>

        <label>
          Category:
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="Sanitation">Sanitation</option>
            <option value="Water">Water</option>
            <option value="Roads">Roads</option>
            <option value="Electricity">Electricity</option>
            <option value="Other">Other</option>
          </select>
        </label>

        <label>
          Description:
          <textarea
            placeholder="Describe the issue..."
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows={3}
          />
        </label>

        <label>
          Upload Image (optional):
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </label>

        <button onClick={handleSubmit} disabled={loading}>
          {loading ? "Submitting..." : "üìç Submit Complaint"}
        </button>

        {location && (
          <p className="location-info">
            üìå Location captured:{" "}
            {location.lat.toFixed(4)}, {location.lng.toFixed(4)}
          </p>
        )}

        {video && <p className="location-info">üé• Video recorded</p>}
      </div>

      {/* CAMERA */}
      <div className="camera-panel">
        <h4>Live Camera</h4>

        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          className="camera-preview"
        />

        <div className="camera-controls">
          {!recording ? (
            <button onClick={startRecording} className="record">
              üî¥ Start Recording
            </button>
          ) : (
            <button onClick={stopRecording} className="stop">
              ‚èπ Stop Recording
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
