import React, { useState, useRef } from "react";

export default function ImageComplaintForm({ refreshComplaints }) {
  const [capturing, setCapturing] = useState(false);
  const [imageBlob, setImageBlob] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [status, setStatus] = useState("");

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Start camera
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      streamRef.current = stream;
      setCapturing(true);
      setStatus("Camera started. Click 'Capture Image' to take photo.");
    } catch (err) {
      console.error(err);
      setStatus("Camera access denied.");
    }
  };

  // Stop camera
  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      setCapturing(false);
      setStatus("Camera stopped.");
    }
  };

  // Capture image
  const captureImage = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      setImageBlob(blob);
      setPreviewUrl(URL.createObjectURL(blob));
      setStatus("Image captured.");
      stopCamera();
    }, "image/png");
  };

  // Capture location
  const captureLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude });
          setStatus("Location captured.");
        },
        () => setStatus("Failed to fetch location.")
      );
    } else {
      setStatus("Geolocation not supported.");
    }
  };

  // Submit complaint
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!imageBlob) return setStatus("Please capture an image.");
    if (!location.lat) return setStatus("Please capture location.");

    const formData = new FormData();
    formData.append("image", imageBlob, "complaint.png");
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await fetch("http://localhost:8000/complaints", {
        method: "POST",
        body: formData
      });

      if (!res.ok) throw new Error(`Server error: ${res.statusText}`);
      const data = await res.json();

      if (data.status === "success") {
        setStatus("‚úÖ Complaint submitted!");
        refreshComplaints(data.complaint);
        setImageBlob(null);
        setPreviewUrl(null);
        setLocation({ lat: null, lng: null });
      } else {
        setStatus(`‚ùå Failed: ${data.detail || "Unknown error"}`);
      }
    } catch (err) {
      console.error(err);
      setStatus(`‚ö†Ô∏è Error submitting complaint: ${err.message}`);
    }
  };

  return (
    <form className="video-complaint-form" onSubmit={handleSubmit}>
      <h3>Submit Image Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Infrastructure">Infrastructure</option>
          <option value="Pothole">Pothole</option>
          <option value="Garbage">Garbage</option>
        </select>
      </label>

      <div>
        {!previewUrl && (
          <>
            <video ref={videoRef} autoPlay style={{ width: "100%", borderRadius: "8px" }}></video>
            {!capturing && <button type="button" onClick={startCamera}>üì∑ Start Camera</button>}
            {capturing && <button type="button" onClick={captureImage}>üì∏ Capture Image</button>}
          </>
        )}

        {previewUrl && (
          <img src={previewUrl} alt="Captured" style={{ width: "100%", borderRadius: "8px", marginTop: "8px" }} />
        )}
        <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
      </div>

      <button type="button" onClick={captureLocation}>üìç Capture Location</button>

      {location.lat && location.lng && (
        <p>Location: {location.lat.toFixed(4)}, {location.lng.toFixed(4)}</p>
      )}

      <button type="submit">Submit Complaint</button>
      <p>{status}</p>
    </form>
  );
}
