import React, { useRef, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000"; // Update to your backend

export default function ImageComplaintForm({ refreshComplaints }) {
  const videoRef = useRef(null);
  const [imageBlob, setImageBlob] = useState(null);
  const [location, setLocation] = useState(null);
  const [category, setCategory] = useState("Sanitation");

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (err) {
      alert("Camera access denied or not available.");
    }
  };

  const captureImage = () => {
    const canvas = document.createElement("canvas");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0);
    canvas.toBlob((blob) => {
      setImageBlob(blob);
    }, "image/jpeg");
  };

  const captureLocation = () => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => {
        alert("Unable to fetch location.");
        console.error(err);
      }
    );
  };

  const handleSubmit = async () => {
    if (!imageBlob || !location) {
      alert("Please capture image and location before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("image", imageBlob, "complaint.jpg");
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await axios.post(`${API}/complaints`, formData);
      alert("Complaint submitted!");
      refreshComplaints(res.data); // adds to the list
    } catch (err) {
      console.error("Submission error:", err);
      alert("Failed to submit complaint.");
    }
  };

  return (
    <div className="form">
      <h3>Submit Image Complaint</h3>
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="Sanitation">Sanitation</option>
        <option value="Water">Water</option>
        <option value="Roads">Roads</option>
        {/* Add more as needed */}
      </select>

      <video ref={videoRef} autoPlay playsInline style={{ width: "100%", borderRadius: "8px" }}></video>
      <button onClick={startCamera}>üì∑ Start Camera</button>
      <button onClick={captureImage}>üñºÔ∏è Capture Image</button>
      <button onClick={captureLocation}>üìç Capture Location</button>

      <button onClick={handleSubmit} style={{ marginTop: "10px" }}>
        Submit Complaint
      </button>
    </div>
  );
}
