import React, { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function ComplaintForm({ refreshComplaints }) {
  const [location, setLocation] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);

  /* ---------------- GET LIVE LOCATION ---------------- */
  useEffect(() => {
    if (!navigator.geolocation) {
      alert("Geolocation not supported");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => {
        alert("Location access denied.");
        console.error(err);
      }
    );
  }, []);

  /* ---------------- SUBMIT HANDLER ---------------- */
  const handleSubmit = async () => {
    if (!location) {
      alert("Location not available.");
      return;
    }

    if (!description.trim()) {
      alert("Please enter a description.");
      return;
    }

    const formData = new FormData();
    formData.append("category", category);
    formData.append("description", description);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);
    if (image) formData.append("image", image);

    try {
      setLoading(true);

      const res = await axios.post(`${API}/complaints`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("‚úÖ Complaint submitted successfully!");

      // Optional: update UI instantly
      if (refreshComplaints && res.data?.complaint) {
        refreshComplaints(res.data.complaint);
      }

      // Reset form
      setDescription("");
      setImage(null);
    } catch (err) {
      console.error("Submission error:", err);
      alert("‚ùå Failed to submit complaint.");
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="video-complaint-form">
      <h3>Submit Complaint</h3>

      <label>
        Category:
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Sanitation">Sanitation</option>
          <option value="Water">Water</option>
          <option value="Roads">Roads</option>
          <option value="Electricity">Electricity</option>
          <option value="Other">Other</option>
        </select>
      </label>

      <label>
        Description:
        <textarea
          placeholder="Describe the issue..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows={3}
        />
      </label>

      <label>
        Upload Image (optional):
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />
      </label>

      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "Submitting..." : "üìç Submit Complaint"}
      </button>

      {location && (
        <p className="location-info">
          üìå Location captured:{" "}
          {location.lat.toFixed(4)}, {location.lng.toFixed(4)}
        </p>
      )}
    </div>
  );
}
