import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix Leaflet default marker issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function MapView({ complaints }) {
  return (
    <MapContainer center={[28.6, 77.2]} zoom={12} style={{ height: "100%", width: "100%" }}>
      <TileLayer
        url={`https://api.mapbox.com/styles/v1/mapbox/streets-v11/tiles/{z}/{x}/{y}?access_token=${process.env.REACT_APP_MAPBOX_TOKEN}`}
        attribution='&copy; <a href="https://www.mapbox.com/">Mapbox</a> contributors'
      />

      {complaints.map((complaint) => (
        <Marker key={complaint.id} position={[complaint.lat, complaint.lng]}>
          <Popup>
            <strong>{complaint.title}</strong>
            <br />
            {complaint.description}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}
