import React, { useState } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

export default function VideoComplaintForm({ refreshComplaints }) {
  const [videoFile, setVideoFile] = useState(null);
  const [category, setCategory] = useState("pothole");
  const [submitting, setSubmitting] = useState(false);

  const handleVideoChange = (e) => {
    setVideoFile(e.target.files[0]);
  };

  const handleSubmit = () => {
    if (!videoFile) {
      alert("Please select a video!");
      return;
    }

    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser");
      return;
    }

    setSubmitting(true);

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        const formData = new FormData();
        formData.append("video", videoFile);
        formData.append("latitude", latitude);
        formData.append("longitude", longitude);
        formData.append("category", category);

        try {
          await axios.post(`${API}/complaints`, formData, {
            headers: { "Content-Type": "multipart/form-data" },
          });
          alert("Complaint submitted!");
          setVideoFile(null);
          refreshComplaints(); // Refresh live map and table
        } catch (err) {
          console.error("Error submitting complaint:", err);
          alert("Failed to submit complaint");
        } finally {
          setSubmitting(false);
        }
      },
      (error) => {
        console.error("Error getting location:", error);
        alert("Could not get your location. Please enable location services.");
        setSubmitting(false);
      }
    );
  };

  return (
    <div className="video-complaint-form" style={{ padding: "12px", background: "#fff", borderRadius: "8px", marginBottom: "12px" }}>
      <h3>Submit a Complaint</h3>
      <label>
        Category:{" "}
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="pothole">Pothole</option>
          <option value="garbage">Garbage</option>
        </select>
      </label>
      <br /><br />
      <input type="file" accept="video/*" onChange={handleVideoChange} />
      <br /><br />
      <button onClick={handleSubmit} disabled={submitting}>
        {submitting ? "Submitting..." : "Submit Complaint"}
      </button>
    </div>
  );
}
