import React, { useEffect, useState } from "react";
import axios from "axios";

import VideoComplaintForm from "./components/VideoComplaintForm";
import Sidebar from "./components/Sidebar";
import Topbar from "./components/Topbar";
import MapView from "./components/MapView";
import ComplaintsTable from "./components/ComplaintsTable";
import AnalyticsPanel from "./components/AnalyticsPanel";

const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

export default function App() {
  const [complaints, setComplaints] = useState([]);
  const [stats, setStats] = useState({});
  const [sidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    fetchAll();
    const interval = setInterval(fetchAll, 15000);
    return () => clearInterval(interval);
  }, []);

  const fetchAll = () => {
    fetchComplaints();
    fetchStats();
  };

  const fetchComplaints = async () => {
    try {
      const res = await axios.get(`${API}/complaints`);
      setComplaints(res.data);
    } catch (err) {
      console.error("Error fetching complaints:", err);
    }
  };

  const fetchStats = async () => {
    try {
      const res = await axios.get(`${API}/analytics`);
      setStats(res.data);
    } catch (err) {
      console.error("Error fetching analytics:", err);
    }
  };

  // Callback to add a new complaint instantly
  const addComplaint = (newComplaint) => {
    setComplaints((prev) => [...prev, newComplaint]);
    fetchStats(); // update analytics immediately
  };

  return (
    <div className="app">
      <div className={`overlay ${sidebarOpen ? "show" : ""}`} onClick={() => setSidebarOpen(false)}></div>
      <div className={`sidebar ${sidebarOpen ? "open" : ""}`}><Sidebar /></div>

      <div className="main">
        <div className="topbar">
          <span className="hamburger" onClick={() => setSidebarOpen(!sidebarOpen)}>&#9776;</span>
          <Topbar stats={stats} />
        </div>

        <div className="content">
          <div className="left-col">
            <MapView complaints={complaints} />
          </div>

          <div className="right-col">
            <VideoComplaintForm refreshComplaints={addComplaint} />

            <AnalyticsPanel stats={stats} complaints={complaints} />
            <ComplaintsTable complaints={complaints} refresh={fetchComplaints} />
          </div>
        </div>
      </div>
    </div>
  );
}
