import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function ImageComplaintForm({ refreshComplaints }) {
  const videoRef = useRef(null);
  const [imageBlob, setImageBlob] = useState(null);
  const [location, setLocation] = useState(null);
  const [category, setCategory] = useState("Sanitation");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude });
      },
      (err) => console.error("Location error:", err)
    );
  }, []);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;

      // Automatically capture location when camera starts
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          });
        },
        (err) => {
          alert("Location access denied.");
          console.error(err);
        }
      );
    } catch (err) {
      alert("Camera access denied or not available.");
    }
  };

  const captureImage = () => {
    const canvas = document.createElement("canvas");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0);
    canvas.toBlob((blob) => {
      setImageBlob(blob);
    }, "image/jpeg");
  };

  const handleSubmit = async () => {
    if (!imageBlob || !location) {
      alert("Please capture image and ensure location is fetched.");
      return;
    }

    const formData = new FormData();
    formData.append("image", imageBlob, "complaint.jpg");
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await axios.post(`${API}/complaints`, formData);
      alert("Complaint submitted!");
      refreshComplaints(res.data.complaint);
      setImageBlob(null);
    } catch (err) {
      console.error("Submission error:", err);
      alert("Failed to submit complaint.");
    }
  };

  return (
    <div className="video-complaint-form">
      <h3>Submit Image Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Water">Water</option>
          <option value="Roads">Roads</option>
        </select>
      </label>

      <video ref={videoRef} autoPlay playsInline />

      <div className="camera-controls">
        <button onClick={startCamera}>üì∑ Start Camera</button>
        <button onClick={captureImage}>üñºÔ∏è Capture Image</button>
        <button onClick={handleSubmit}>üì§ Submit</button>
      </div>

      {location && (
        <div className="live-location-pin">
          üìç Live Location: {location.lat.toFixed(4)}, {location.lng.toFixed(4)}
        </div>
      )}
    </div>
  );
}
