import React from "react";

export default function Sidebar({
  setActivePage,
  activePage,
  closeSidebar,
  logout,
}) {
  const goTo = (page) => {
    setActivePage(page);
    if (closeSidebar) closeSidebar();
  };

  return (
    <aside className="sidebar">
      {/* BRAND */}
      <div className="sidebar-brand">
        <div className="brand-logo">üü¶</div>
        <div>
          <h2>SAHAAY</h2>
          <span className="sidebar-subtitle">Admin Dashboard</span>
        </div>
      </div>

      {/* NAVIGATION */}
      <nav className="sidebar-nav">
        <button
          className={`nav-item ${activePage === "reports" ? "active" : ""}`}
          onClick={() => goTo("reports")}
        >
          <span className="icon">üìã</span>
          <span className="label">Reports</span>
        </button>

        <button
          className={`nav-item ${activePage === "map" ? "active" : ""}`}
          onClick={() => goTo("map")}
        >
          <span className="icon">üó∫Ô∏è</span>
          <span className="label">Map View</span>
        </button>

        <button
          className={`nav-item ${activePage === "file" ? "active" : ""}`}
          onClick={() => goTo("file")}
        >
          <span className="icon">üìù</span>
          <span className="label">File Complaint</span>
        </button>

        <div className="sidebar-divider" />

        <button className="nav-item logout" onClick={logout}>
          <span className="icon">üö™</span>
          <span className="label">Logout</span>
        </button>
      </nav>

      {/* FOOTER */}
      <div className="sidebar-footer">
        <span>¬© {new Date().getFullYear()} SAHAAY</span>
      </div>
    </aside>
  );
}
