import React, { useState, useRef } from "react";
import axios from "axios";

const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

export default function VideoComplaintForm({ refreshComplaints }) {
  const [recording, setRecording] = useState(false);
  const [videoURL, setVideoURL] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [category, setCategory] = useState("pothole");
  const [submitting, setSubmitting] = useState(false);

  const videoRef = useRef();
  const chunksRef = useRef([]);

  // Start recording
  const startRecording = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      alert("Camera access is not supported by your browser");
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();

      const recorder = new MediaRecorder(stream);
      recorder.ondataavailable = (e) => {
        chunksRef.current.push(e.data);
      };
      recorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: "video/webm" });
        setVideoURL(URL.createObjectURL(blob));
        chunksRef.current = [];
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
    } catch (err) {
      console.error("Error accessing camera:", err);
      alert("Could not access camera");
    }
  };

  // Stop recording
  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      setRecording(false);
    }
  };

  // Submit video complaint
  const handleSubmit = () => {
    if (!videoURL) {
      alert("No video recorded!");
      return;
    }

    if (!navigator.geolocation) {
      alert("Geolocation is not supported by your browser");
      return;
    }

    setSubmitting(true);

    navigator.geolocation.getCurrentPosition(async (position) => {
      const { latitude, longitude } = position.coords;

      // Convert recorded video to blob
      const response = await fetch(videoURL);
      const blob = await response.blob();

      const formData = new FormData();
      formData.append("video", blob, "complaint.webm");
      formData.append("latitude", latitude);
      formData.append("longitude", longitude);
      formData.append("category", category);

      try {
        await axios.post(`${API}/complaints`, formData, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("Complaint submitted!");
        setVideoURL(null);
        refreshComplaints();
      } catch (err) {
        console.error("Error submitting complaint:", err);
        alert("Failed to submit complaint");
      } finally {
        setSubmitting(false);
      }
    }, (error) => {
      console.error("Error getting location:", error);
      alert("Could not get your location. Please enable location services.");
      setSubmitting(false);
    });
  };

  return (
    <div className="video-complaint-form" style={{ padding: "12px", background: "#fff", borderRadius: "8px", marginBottom: "12px" }}>
      <h3>Submit a Video Complaint</h3>

      <label>
        Category:{" "}
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="pothole">Pothole</option>
          <option value="garbage">Garbage</option>
        </select>
      </label>

      <br /><br />

      <video ref={videoRef} style={{ width: "100%", borderRadius: "8px" }} controls={!!videoURL} />

      <br /><br />

      {!recording ? (
        <button onClick={startRecording}>Start Recording</button>
      ) : (
        <button onClick={stopRecording}>Stop Recording</button>
      )}

      <button
        onClick={handleSubmit}
        disabled={submitting || recording || !videoURL}
        style={{ marginLeft: "12px" }}
      >
        {submitting ? "Submitting..." : "Submit Complaint"}
      </button>
    </div>
  );
}
