import React, { useRef, useState } from "react";
import axios from "axios";

const API = "http://localhost:8000";

export default function ImageComplaintForm({ refreshComplaints }) {
  const videoRef = useRef(null);
  const [imageBlob, setImageBlob] = useState(null);
  const [category, setCategory] = useState("Sanitation");
  const [location, setLocation] = useState(null);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;

      // Auto-fetch location when camera starts
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          });
        },
        (err) => {
          console.error("Location error:", err);
          alert("Unable to fetch location");
        }
      );
    } catch (err) {
      alert("Camera access denied or unavailable.");
      console.error(err);
    }
  };

  const captureImage = () => {
    const canvas = document.createElement("canvas");
    const video = videoRef.current;

    if (!video || video.readyState < 2) {
      alert("Camera not ready. Try again.");
      return;
    }

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    canvas.toBlob((blob) => {
      if (blob) setImageBlob(blob);
    }, "image/jpeg");
  };

  const handleSubmit = async () => {
    if (!imageBlob || !location) {
      alert("Capture both image and location before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("image", imageBlob, "complaint.jpg");
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      const res = await axios.post(`${API}/complaints`, formData);
      alert("Complaint submitted successfully!");

      // Clear captured image and refresh list
      setImageBlob(null);
      refreshComplaints(res.data.complaint);
    } catch (err) {
      console.error("Submit error:", err);
      alert("Submission failed.");
    }
  };

  return (
    <div className="video-complaint-form">
      <h3>Submit Image Complaint</h3>

      <label>
        Category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Sanitation">Sanitation</option>
          <option value="Water">Water</option>
          <option value="Roads">Roads</option>
        </select>
      </label>

      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        style={{ width: "100%", borderRadius: "8px", background: "#000" }}
      ></video>

      <div className="camera-controls">
        <button onClick={startCamera}>üì∑ Start Camera</button>
        <button onClick={captureImage}>üñºÔ∏è Capture Image</button>
        <button onClick={handleSubmit}>üì§ Submit</button>
      </div>

      {location && (
        <div className="live-location-pin">
          üìç Live Location: {location.lat.toFixed(4)}, {location.lng.toFixed(4)}
        </div>
      )}
    </div>
  );
}
