import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

/* ---------------- ICON SETUP ---------------- */

// Fix Leaflet default icon issue
delete L.Icon.Default.prototype._getIconUrl;

// Priority-based icons
const redIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/red-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const yellowIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/yellow-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const greenIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/green-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const getIconByPriority = (priority = "Medium") => {
  const p = priority.toLowerCase();
  if (p === "high") return redIcon;
  if (p === "medium") return yellowIcon;
  return greenIcon;
};

/* ---------------- COMPONENT ---------------- */

export default function MapView({ complaints }) {
  const [liveLocation, setLiveLocation] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLiveLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      () => {} // silently ignore
    );
  }, []);

  // Fallback city center (Delhi)
  const defaultCenter = [28.6139, 77.2090];

  return (
    <div className="map-container">
      <MapContainer
        center={defaultCenter}
        zoom={12}
        className="leaflet-map"
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* REPORT PINS */}
        {complaints &&
          complaints.map((c) => {
            if (!c.lat || !c.lng) return null;

            return (
              <Marker
                key={c.id}
                position={[Number(c.lat), Number(c.lng)]}
                icon={getIconByPriority(c.priority)}
              >
                <Popup>
                  <strong>{c.category || "Civic Issue"}</strong>

                  <p><b>Priority:</b> {c.priority || "Medium"}</p>
                  <p><b>Status:</b> {c.status || "Pending"}</p>
                  <p><b>Area:</b> {c.area || "Unknown Ward"}</p>

                  {c.image && (
                    <img
                      src={`http://localhost:8000/uploads/${c.image}`}
                      alt="Report"
                      style={{
                        width: "200px",
                        borderRadius: "6px",
                        marginTop: "6px",
                      }}
                    />
                  )}
                </Popup>
              </Marker>
            );
          })}

        {/* ADMIN LOCATION */}
        {liveLocation && (
          <Marker position={[liveLocation.lat, liveLocation.lng]}>
            <Popup>
              <strong>Admin Location</strong>
              <p>
                {liveLocation.lat.toFixed(4)}, {liveLocation.lng.toFixed(4)}
              </p>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}
