import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix Leaflet default icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function MapView({ complaints }) {
  return (
    <div className="map-container">
      <MapContainer key={complaints.length} center={[28.6, 77.2]} zoom={12} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors'
        />

        {complaints
          .filter(c => c.lat && c.lng)
          .map(c => (
            <Marker key={c.id} position={[Number(c.lat), Number(c.lng)]}>
              <Popup>
                <strong>{c.title}</strong>
                <p>Category: {c.category}</p>
                <p>Status: {c.status}</p>
                {c.video && (
                  <video
                    src={`http://localhost:8000/uploads/${c.video}`}
                    controls
                    width="200"
                    style={{ borderRadius: "6px", marginTop: "6px" }}
                  />
                )}
              </Popup>
            </Marker>
          ))}
      </MapContainer>
    </div>
  );
}
