import React, { useEffect, useState } from "react";
import Sidebar from "./components/Sidebar";
import Topbar from "./components/Topbar";
import MapView from "./components/MapView";
import ComplaintsTable from "./components/ComplaintsTable";
import AnalyticsPanel from "./components/AnalyticsPanel";
import axios from "axios";

// Use your FastAPI base URL directly
const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

export default function App() {
  const [complaints, setComplaints] = useState([]);
  const [stats, setStats] = useState({});
  const [sidebarOpen, setSidebarOpen] = useState(false); // Sidebar toggle state

  // Fetch once + start polling every 15 seconds
  useEffect(() => {
    fetchAll();

    const interval = setInterval(() => {
      fetchAll();
    }, 15000); // every 15s

    return () => clearInterval(interval); // cleanup
  }, []);

  const fetchAll = () => {
    fetchComplaints();
    fetchStats();
  };

  const fetchComplaints = async () => {
    try {
      const res = await axios.get(`${API}/complaints`);
      setComplaints(res.data);
    } catch (err) {
      console.error("Error fetching complaints:", err);
    }
  };

  const fetchStats = async () => {
    try {
      const res = await axios.get(`${API}/analytics`);
      setStats(res.data);
    } catch (err) {
      console.error("Error fetching analytics:", err);
    }
  };

  return (
    <div className="app">
      {/* Overlay for mobile sidebar */}
      <div
        className={`overlay ${sidebarOpen ? "show" : ""}`}
        onClick={() => setSidebarOpen(false)}
      ></div>

      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
        <Sidebar />
      </div>

      {/* Main content */}
      <div className="main">
        {/* Topbar with hamburger for mobile */}
        <div className="topbar">
          <span
            className="hamburger"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            &#9776;
          </span>
          <Topbar stats={stats} />
        </div>

        <div className="content">
          <div className="left-col">
            <MapView complaints={complaints} />
          </div>
          <div className="right-col">
            <AnalyticsPanel stats={stats} complaints={complaints} />
            <ComplaintsTable complaints={complaints} refresh={fetchComplaints} />
          </div>
        </div>
      </div>
    </div>
  );
}
