import React, { useEffect, useState } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  Popup,
  Circle
} from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

/* ---------------- ICON SETUP ---------------- */

delete L.Icon.Default.prototype._getIconUrl;

const redIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/red-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const yellowIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/yellow-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const greenIcon = new L.Icon({
  iconUrl: "https://maps.google.com/mapfiles/ms/icons/green-dot.png",
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const getIconByPriority = (priority = "Medium") => {
  const p = priority.toLowerCase();
  if (p === "high") return redIcon;
  if (p === "medium") return yellowIcon;
  return greenIcon;
};

/* ---------------- COMPONENT ---------------- */

export default function MapView({ complaints }) {
  const [liveLocation, setLiveLocation] = useState(null);
  const [selectedReport, setSelectedReport] = useState(null);

  // Filters
  const [priorityFilter, setPriorityFilter] = useState("All");
  const [showHeatmap, setShowHeatmap] = useState(false);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLiveLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      () => {}
    );
  }, []);

  const defaultCenter = [28.6139, 77.2090];

  const filteredComplaints =
    priorityFilter === "All"
      ? complaints
      : complaints.filter(
          (c) =>
            (c.priority || "Medium").toLowerCase() ===
            priorityFilter.toLowerCase()
        );

  return (
    <div className="map-container">
      {/* -------- MAP CONTROLS -------- */}
      <div className="map-controls">
        <select
          value={priorityFilter}
          onChange={(e) => setPriorityFilter(e.target.value)}
        >
          <option value="All">All Priorities</option>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>

        <label style={{ marginLeft: "10px" }}>
          <input
            type="checkbox"
            checked={showHeatmap}
            onChange={() => setShowHeatmap(!showHeatmap)}
          />
          Heatmap
        </label>
      </div>

      <MapContainer center={defaultCenter} zoom={12} className="leaflet-map">
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* -------- REPORT PINS -------- */}
        {filteredComplaints.map((c) => {
          if (!c.lat || !c.lng) return null;

          return (
            <Marker
              key={c.id}
              position={[Number(c.lat), Number(c.lng)]}
              icon={getIconByPriority(c.priority)}
              eventHandlers={{
                click: () => setSelectedReport(c),
              }}
            >
              <Popup>
                <strong>{c.category || "Civic Issue"}</strong>
                <p>Status: {c.status || "Pending"}</p>
                <p>Priority: {c.priority || "Medium"}</p>
              </Popup>
            </Marker>
          );
        })}

        {/* -------- HEATMAP (SIMULATED) -------- */}
        {showHeatmap &&
          filteredComplaints.map((c) => (
            <Circle
              key={`heat-${c.id}`}
              center={[Number(c.lat), Number(c.lng)]}
              radius={300}
              pathOptions={{ color: "red", fillOpacity: 0.2 }}
            />
          ))}

        {/* -------- ADMIN LOCATION -------- */}
        {liveLocation && (
          <Marker position={[liveLocation.lat, liveLocation.lng]}>
            <Popup>Admin Location</Popup>
          </Marker>
        )}
      </MapContainer>

      {/* -------- REPORT DETAIL PANEL -------- */}
      {selectedReport && (
        <div className="report-detail-panel">
          <h3>Report Details</h3>
          <p><b>Category:</b> {selectedReport.category}</p>
          <p><b>Status:</b> {selectedReport.status}</p>
          <p><b>Priority:</b> {selectedReport.priority}</p>
          <p><b>Area:</b> {selectedReport.area || "Unknown"}</p>

          {selectedReport.image && (
            <video
              src={`http://localhost:8000/uploads/${selectedReport.image}`}
              controls
              width="100%"
            />
          )}

          <button onClick={() => setSelectedReport(null)}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}
