import React, { useState, useEffect } from "react";
import axios from "axios";

// Use same API base URL as App.js
const API = process.env.REACT_APP_API_URL || "http://localhost:8000";

export default function VideoComplaintForm({ refreshComplaints }) {
  const [videoFile, setVideoFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [category, setCategory] = useState("pothole");
  const [location, setLocation] = useState({ lat: null, lng: null });
  const [submitting, setSubmitting] = useState(false);

  // Auto-fetch user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          });
        },
        (err) => {
          console.error("Geolocation error:", err);
          alert("Could not fetch your location. Please enable location access.");
        }
      );
    }
  }, []);

  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setVideoFile(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!videoFile || !location.lat || !location.lng) {
      alert("Please select a video and allow location access.");
      return;
    }

    setSubmitting(true);
    const formData = new FormData();
    formData.append("video", videoFile);
    formData.append("category", category);
    formData.append("lat", location.lat);
    formData.append("lng", location.lng);

    try {
      await axios.post(`${API}/complaints`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Complaint submitted successfully!");
      setVideoFile(null);
      setPreview(null);
      refreshComplaints();
    } catch (err) {
      console.error("Submit failed:", err);
      alert("Video submit failed. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="video-complaint-form">
      <h3>Submit a Complaint</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Category:
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="pothole">Pothole</option>
            <option value="garbage">Garbage</option>
            <option value="other">Other</option>
          </select>
        </label>

        <label>
          Upload Video:
          <input type="file" accept="video/*" onChange={handleVideoChange} />
        </label>

        {preview && (
          <video src={preview} controls />
        )}

        <button type="submit" disabled={submitting}>
          {submitting ? "Submitting..." : "Submit Complaint"}
        </button>
      </form>
    </div>
  );
}
