import React from "react";
import Map, { Marker, NavigationControl } from "react-map-gl";

export default function MapView({complaints}){
  const token = process.env.REACT_APP_MAPBOX_TOKEN;

  if(!token){
    return <div className="map-placeholder">Set REACT_APP_MAPBOX_TOKEN in .env</div>;
  }

  return (
    <div className="map-container">
      <Map
        initialViewState={{ longitude: 77.2090, latitude: 28.6139, zoom: 11 }}
        style={{width: "100%", height: "100%"}}
        mapStyle="mapbox://styles/mapbox/satellite-streets-v11"

        mapboxAccessToken={token}
      >
        <NavigationControl position="top-left" />
        {complaints && complaints.map(c => (
          <Marker key={c.id} longitude={c.longitude} latitude={c.latitude} anchor="bottom">
            <div className={`pin ${c.category ?? ""}`} title={c.title}>üìç</div>
          </Marker>
        ))}
      </Map>
    </div>
  );
}
