import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix Leaflet default icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function MapView({ complaints }) {
  const [liveLocation, setLiveLocation] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        setLiveLocation({
          lat: pos.coords.latitude,
          lng: pos.coords.longitude,
        });
      },
      (err) => console.error("Map location error:", err)
    );
  }, []);

  return (
    <div className="map-container">
      <MapContainer center={[28.6, 77.2]} zoom={12} className="leaflet-map">
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors'
        />

        {complaints.map((c) => (
          <Marker key={c.id} position={[c.lat, c.lng]}>
            <Popup>
              <strong>{c.title}</strong>
              <br />
              <p>Category: {c.category}</p>
              <p>Status: {c.status}</p>
              {c.image && (
                <img
                  src={`http://localhost:8000/uploads/${c.image}`}
                  alt="Complaint"
                  style={{ width: "200px", borderRadius: "6px", marginTop: "6px" }}
                />
              )}
            </Popup>
          </Marker>
        ))}

        {liveLocation && (
          <Marker position={[liveLocation.lat, liveLocation.lng]}>
            <Popup>
              <strong>Your Location</strong>
              <p>{liveLocation.lat.toFixed(4)}, {liveLocation.lng.toFixed(4)}</p>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}
